import styled from "styled-components"
import React from "react"
import axios from "axios"
import { TopText, Container } from "./Styled"


function Seat({number, id}){

    const [selected, setSelected] = React.useState(false)

    function clickhandler(){
        !selected ? setSelected(false) : setSelected(true)

        console.log(selected)
    }

    const colorselected = "#8DD7CF"
    const coloravaliable = "#C3CFD9"
    const coloraUnvaliable = "#FBE192"






    return( <StyledSeat selected ={selected[index]} colorselected={colorselected}
        coloravaliable = {coloravaliable} onClick={() => clickhandler(index)}>
        {number}
        </StyledSeat>)
}

export default function Assentos(){
    const {idSessao} = useParams()
    const [assentos, setAssentos] = React.useState([])


    React.useEffect(() => {

        const promise = 
        axios.get(`https://mock-api.driven.com.br/api/v5/cineflex/showtimes/${idSessao}/seats`)

        promise.then(response => {
          setAssentos(response.data.seats)
        })

      }, [clickhandler])



    return(<Container>
        <TopText>
        Selecione os Assentos
        </TopText>
        <Seats>
        {assentos.map((assento) =>
        <Seat number={assento.name} id= {assento.id}></Seat>)}

        </Seats>
        </Container>)
}

const StyledSeat = styled.div`
display: flex;
align-items: center;
justify-content: center;
background-color: ${props => props.selected ? props.colorselected : props.coloravaliable};
height: 26px;
width: 26px;
border-radius: 12px;
margin-left: 10px;
margin-top: 20px;
`

const Seats = styled.div`
display: flex;
flex-wrap: wrap;
`
