export default function Sucesso({reserva}){


    return(
    <Container>
    <TopText>
     Pedido feito com sucesso
    </TopText>
    <Msg>
     Filme e sess√£o
     <h1>{reserva.filme}</h1>
     <h1>{reserva.data} {reserva.horario}</h1>
    </Msg>
    <Msg>
    Ingressos
    {reserva.assentos.map(assento => <h1>Assento {assento}</h1>)}
    </Msg>
    <Msg>
    Comprador
