import { useParams, Link } from "react-router-dom"
import styled from "styled-components"
import React from "react"
import axios from "axios"
import { TopText, Container } from "./Styled"

export default function Assentos(){
    const {idSessao} = useParams()
    const [assentos, setAssentos] = React.useState([])
    const [selected, SetSelected] = React.useState(false)
    function clickhandler(){
        {!selected? SetSelected(true) : SetSelected(false)}
    }

    const colorselected = "#8DD7CF"
    const coloravaliable = "#C3CFD9"
    React.useEffect(() => {


        console.log(selected)
        const promise = 
        axios.get(`https://mock-api.driven.com.br/api/v5/cineflex/showtimes/${idSessao}/seats`)

        promise.then(response => {
          setAssentos(response.data.seats)
        })

      }, [])

      console.log(assentos)

    return(<Container>
        <TopText>
        Selecione os Assentos
        </TopText>
        <Seats>
        {assentos.map(assento =>
        <Seat selected ={selected} colorselected={colorselected}
        coloravaliable = {coloravaliable} onClick={clickhandler()}>
        {assento.id}
        </Seat> )}

        </Seats>
        </Container>)
}

const Seat = styled.div`
display: flex;
align-items: center;
justify-content: center;
background-color: ${props => props.selected ? colorselected : coloravaliable};
height: 26px;
width: 26px;
border-radius: 12px;
margin-left: 10px;
margin-top: 20px;
`

const Seats = styled.div`
display: flex;
flex-wrap: wrap;
`
